def select_neighbor(archive: List[Tuple[np.ndarray, Tuple[float, float]]], weight_lst: np.ndarray, value1_lst: np.ndarray, value2_lst: np.ndarray, capacity: float) -> np.ndarray:
    if not archive:
        return np.zeros_like(weight_lst, dtype=int)

    # ε-dominance ranking to select diverse high-quality solutions
    epsilon = 0.1  # ε parameter for ε-dominance
    ranked_solutions = []
    for sol, obj in archive:
        dominated = False
        for other_sol, other_obj in archive:
            if (other_obj[0] >= obj[0] - epsilon and other_obj[1] >= obj[1] - epsilon and
                (other_obj[0] > obj[0] or other_obj[1] > obj[1])):
                dominated = True
                break
        if not dominated:
            ranked_solutions.append((sol, obj))

    if not ranked_solutions:
        ranked_solutions = archive

    # Select solution with highest ε-dominated rank (sum of objectives)
    selected = max(ranked_solutions, key=lambda x: sum(x[1]))
    base_solution = selected[0].copy()
    new_solution = base_solution.copy()
    current_weight = np.sum(weight_lst * base_solution)

    # Calculate weighted marginal gains
    weight_factor = 0.6 if current_weight > 0.8 * capacity else 0.4
    marginal_gain = (weight_factor * value1_lst + (1 - weight_factor) * value2_lst) / (weight_lst + 1e-6)

    # Sort items by marginal gain (descending)
    sorted_indices = np.argsort(-marginal_gain)

    # Local search with targeted flips
    for i in sorted_indices:
        if new_solution[i] == 1:
            # Try removing item
            temp_weight = current_weight - weight_lst[i]
            if temp_weight <= capacity:
                new_solution[i] = 0
                current_weight = temp_weight
                break
        else:
            # Try adding item
            temp_weight = current_weight + weight_lst[i]
            if temp_weight <= capacity:
                new_solution[i] = 1
                current_weight = temp_weight
                break

    # Adaptive perturbation
    perturbation_prob = 0.3 if current_weight > 0.7 * capacity else 0.15
    if np.random.rand() < perturbation_prob:
        # Select 3 random items to perturb
        perturb_indices = np.random.choice(len(weight_lst), size=min(3, len(weight_lst)), replace=False)
        for i in perturb_indices:
            if new_solution[i] == 1:
                temp_weight = current_weight - weight_lst[i]
                if temp_weight <= capacity:
                    new_solution[i] = 0
                    current_weight = temp_weight
            else:
                temp_weight = current_weight + weight_lst[i]
                if temp_weight <= capacity:
                    new_solution[i] = 1
                    current_weight = temp_weight

    return new_solution

def select_neighbor(archive: List[Tuple[np.ndarray, Tuple[float, float]]], weight_lst: np.ndarray, value1_lst: np.ndarray, value2_lst: np.ndarray, capacity: float) -> np.ndarray:
    if not archive:
        return np.zeros_like(weight_lst, dtype=int)

    # Hybrid selection: ε-dominance ranking combined with objective diversity
    def hybrid_rank(solutions):
        ranks = {}
        for i, (sol_i, obj_i) in enumerate(solutions):
            rank = 0
            for j, (sol_j, obj_j) in enumerate(solutions):
                if i != j:
                    # ε-dominance component
                    if obj_j[0] >= obj_i[0] and obj_j[1] >= obj_i[1]:
                        if obj_j[0] > obj_i[0] or obj_j[1] > obj_i[1]:
                            rank += 1
                    # Objective diversity component
                    rank += abs(obj_i[0] - obj_j[0]) + abs(obj_i[1] - obj_j[1])
            ranks[i] = rank
        return ranks

    ranks = hybrid_rank(archive)
    sorted_indices = sorted(ranks.keys(), key=lambda x: ranks[x])
    top_30_percent = max(1, len(archive) // 3)
    selected_idx = random.choice(sorted_indices[:top_30_percent])
    base_solution = archive[selected_idx][0].copy()
    new_solution = base_solution.copy()
    current_weight = np.sum(weight_lst * new_solution)

    # Dynamic marginal gain calculation with adaptive weighting
    weight_factor = 0.5 if current_weight > 0.6 * capacity else 0.6
    marginal_gain = (weight_factor * value1_lst + (1 - weight_factor) * value2_lst) / (weight_lst + 1e-6)
    sorted_items = np.argsort(-marginal_gain)

    # Priority-based flips: first try to add high-gain items, then remove low-gain items
    for i in sorted_items:
        if new_solution[i] == 0:
            temp_weight = current_weight + weight_lst[i]
            if temp_weight <= capacity:
                new_solution[i] = 1
                current_weight = temp_weight
                break

    if current_weight < capacity:
        for i in sorted_items:
            if new_solution[i] == 1:
                temp_weight = current_weight - weight_lst[i]
                if temp_weight <= capacity:
                    new_solution[i] = 0
                    current_weight = temp_weight
                    break

    # Adaptive perturbation based on marginal gains and capacity
    perturbation_prob = 0.3 if current_weight > 0.7 * capacity else 0.15
    if random.random() < perturbation_prob:
        candidate_indices = [i for i in sorted_items[:min(10, len(sorted_items))]
                           if (new_solution[i] == 1 and current_weight - weight_lst[i] <= capacity) or
                              (new_solution[i] == 0 and current_weight + weight_lst[i] <= capacity)]
        if candidate_indices:
            num_swaps = random.randint(1, min(3, len(candidate_indices)))
            swap_indices = random.sample(candidate_indices, num_swaps)
            for i in swap_indices:
                new_solution[i] = 1 - new_solution[i]
                current_weight += (1 if new_solution[i] == 1 else -1) * weight_lst[i]

    # Final feasibility check
    while np.sum(weight_lst * new_solution) > capacity:
        excess_items = [i for i in range(len(new_solution)) if new_solution[i] == 1]
        if not excess_items:
            break
        excess_items_sorted = sorted(excess_items, key=lambda x: marginal_gain[x])
        remove_idx = excess_items_sorted[0]
        new_solution[remove_idx] = 0

    return new_solution
