    # Calculate dominance counts for each solution
    dominance_counts = []
    for i, (sol_i, obj_i) in enumerate(archive):
        count = 0
        for j, (sol_j, obj_j) in enumerate(archive):
            if i != j and (obj_j[0] >= obj_i[0] and obj_j[1] >= obj_i[1]) and (obj_j[0] > obj_i[0] or obj_j[1] > obj_i[1]):
                count += 1
        dominance_counts.append(count)

    # Select solution with highest dominance count (most crowded)
    selected_idx = np.argmax(dominance_counts)
    base_solution = archive[selected_idx][0].copy()
    new_solution = base_solution.copy()

    # Calculate current total weight
    current_weight = np.sum(weight_lst * new_solution)

    # Compute normalized impact scores (considering both objectives and weight efficiency)
    impact_scores = (value1_lst + value2_lst) / (weight_lst + 1e-6)
    normalized_scores = impact_scores / (np.max(impact_scores) + 1e-6)

    # Adaptive replacement strategy: replace items with low impact with high impact items
    low_impact_mask = normalized_scores < 0.3
    high_impact_items = np.where(normalized_scores >= 0.7)[0]

    if np.any(low_impact_mask) and len(high_impact_items) > 0:
        low_impact_indices = np.where(low_impact_mask)[0]
        for idx in low_impact_indices:
            if new_solution[idx] == 1:
                # Try to replace with a high impact item
                for high_idx in high_impact_items:
