                        break

    # Probabilistic insertion/removal of high impact items
    for idx in high_impact_items:
        if new_solution[idx] == 0 and np.random.rand() < 0.4 * normalized_scores[idx]:
            if current_weight + weight_lst[idx] <= capacity:
                new_solution[idx] = 1
                current_weight += weight_lst[idx]
        elif new_solution[idx] == 1 and np.random.rand() < 0.2 * (1 - normalized_scores[idx]):
            new_solution[idx] = 0
            current_weight -= weight_lst[idx]

    # Dynamic weight adjustment to maintain feasibility
    if current_weight > capacity:
        excess_weight = current_weight - capacity
        # Remove items with lowest impact until feasible
        sorted_indices = np.argsort(normalized_scores)
        for idx in sorted_indices:
            if new_solution[idx] == 1:
                new_solution[idx] = 0
                excess_weight -= weight_lst[idx]
                if excess_weight <= 0:
                    break

    return new_solution

