                crowding[sorted_idx[i]] += (objectives[sorted_idx[i+1], m] - objectives[sorted_idx[i-1], m]) / (objectives[sorted_idx[-1], m] - objectives[sorted_idx[0], m] + 1e-6)
        selected_idx = np.argmax(crowding)
    else:
        selected_idx = 0

    base_solution = archive[selected_idx][0].copy()
    new_solution = base_solution.copy()
    current_weight = np.sum(weight_lst * new_solution)

    # Step 2: Calculate objective dominance
    obj1_dominance = np.mean(value1_lst) / (np.mean(value2_lst) + 1e-6)
    obj2_dominance = np.mean(value2_lst) / (np.mean(value1_lst) + 1e-6)

    # Step 3: Dynamic flip operation based on dominance
    if obj1_dominance > obj2_dominance:
        # Prioritize objective 1 when more dominant
        marginal = (value1_lst * 0.7 + value2_lst * 0.3) / (weight_lst + 1e-6)
    else:
