                new_solution[idx] = 1
                current_weight += weight_lst[idx]

    # Step 5: Adaptive perturbation based on solution density
    density = np.sum(new_solution) / len(new_solution)
    if density < 0.3:  # Sparse solution - add more items
        available = np.where(new_solution == 0)[0]
        if len(available) > 0:
            perturb_candidates = np.argsort(-marginal)[-min(3, len(available)):]
            for idx in perturb_candidates:
