def select_neighbor(archive: List[Tuple[np.ndarray, Tuple[float, float]]], instance: np.ndarray, distance_matrix_1: np.ndarray, distance_matrix_2: np.ndarray) -> np.ndarray:
    if not archive:
        raise ValueError("Archive is empty.")

    # Select a solution with high potential for improvement (e.g., not dominated by others)
    selected_solution = None
    for sol, obj in archive:
        is_dominated = False
        for other_sol, other_obj in archive:
            if (other_obj[0] <= obj[0] and other_obj[1] <= obj[1] and (other_obj[0] < obj[0] or other_obj[1] < obj[1])):
                is_dominated = True
                break
        if not is_dominated:
            selected_solution = sol.copy()
            break
    if selected_solution is None:
        selected_solution = random.choice(archive)[0].copy()

    # Hybrid local search: combine 3-opt with a customized edge swap
    new_solution = selected_solution.copy()
    n = len(new_solution)

    # Randomly select three distinct indices
    a, b, c = sorted(random.sample(range(n), 3))

    # Apply 3-opt: reverse the middle segment
    new_solution[b:c+1] = new_solution[b:c+1][::-1]

    # Apply customized edge swap: swap edges between non-adjacent segments
    if a != 0 and c != n-1:
        if random.random() < 0.5:
            # Swap edges between (a, b) and (c, a)
            new_solution[a], new_solution[c] = new_solution[c], new_solution[a]
        else:
            # Swap edges between (b, c) and (a, b)
            new_solution[b], new_solution[c] = new_solution[c], new_solution[b]

    # Ensure the solution is valid (no duplicates)
    if len(set(new_solution)) != n:
        # If invalid, revert to the original solution
        new_solution = selected_solution.copy()

    return new_solution
