def select_neighbor(archive: List[Tuple[np.ndarray, Tuple[float, float]]], coords: np.ndarray, demand: np.ndarray, distance_matrix: np.ndarray, capacity: float) -> np.ndarray:
    archive_sorted = sorted(archive, key=lambda x: (-x[1][1], x[1][0]))
    base_solution = archive_sorted[0][0].copy()
    new_solution = [route.copy() for route in base_solution]

    if len(new_solution) < 1:
        return new_solution

    longest_route_idx = max(range(len(new_solution)), key=lambda i: len(new_solution[i]))
    longest_route = new_solution[longest_route_idx]

    if len(longest_route) <= 3:
        return new_solution

    depot_coords = coords[0]
    all_customers = np.array([c for route in new_solution for c in route[1:-1]])
    customer_coords = coords[all_customers]

    vectors = customer_coords - depot_coords
    angles = np.arctan2(vectors[:, 1], vectors[:, 0])

    demand_threshold = capacity * 0.7
    high_demand_customers = all_customers[demand[all_customers] > demand_threshold]

    if len(high_demand_customers) < 1:
        return new_solution

    high_demand_coords = coords[high_demand_customers]
    high_demand_angles = np.arctan2(high_demand_coords[:, 1] - depot_coords[1], high_demand_coords[:, 0] - depot_coords[0])

    segments = []
    for i in range(1, len(longest_route) - 1):
        for j in range(i + 1, len(longest_route)):
            segment = longest_route[i:j+1]
            segment_demand = np.sum(demand[segment])
            if segment_demand <= capacity and segment_demand > 0:
                segment_coords = coords[segment]
                segment_angles = np.arctan2(segment_coords[:, 1] - depot_coords[1], segment_coords[:, 0] - depot_coords[0])
                avg_angle = np.mean(segment_angles)
                segments.append((segment, segment_demand, avg_angle))

    if not segments:
        return new_solution

    best_segment, best_segment_demand, best_segment_angle = max(segments, key=lambda x: x[1])

    new_route = np.concatenate([[0], best_segment, [0]])
    new_solution.append(new_route)

    for c in best_segment:
        for i, route in enumerate(new_solution[:-1]):
            if c in route:
                new_solution[i] = np.delete(route, np.where(route == c)[0][0])
                break

    for i in reversed(range(len(new_solution))):
        if len(new_solution[i]) <= 2:
            new_solution.pop(i)

    for i in range(len(new_solution)):
        route = new_solution[i]
        if len(route) > 2:
            for j in range(1, len(route) - 1):
                cust = route[j]
                if cust in high_demand_customers:
                    angle_diff = min(abs(angles[cust-1] - best_segment_angle), 2*np.pi - abs(angles[cust-1] - best_segment_angle))
                    if angle_diff < np.pi/3:
                        for k in range(len(new_solution)):
                            if k != i:
                                other_route = new_solution[k]
                                if np.sum(demand[other_route[1:-1]]) + demand[cust] <= capacity:
                                    best_pos = 1
                                    min_dist = float('inf')
                                    for pos in range(1, len(other_route)):
                                        dist = distance_matrix[other_route[pos-1]][cust] + distance_matrix[cust][other_route[pos]] - distance_matrix[other_route[pos-1]][other_route[pos]]
                                        if dist < min_dist:
                                            min_dist = dist
                                            best_pos = pos
                                    new_solution[k] = np.concatenate([
                                        other_route[:best_pos],
                                        [cust],
                                        other_route[best_pos:]
                                    ])
                                    new_solution[i] = np.delete(route, j)
                                    break

    return new_solution

def select_neighbor(archive: List[Tuple[np.ndarray, Tuple[float, float]]], coords: np.ndarray, demand: np.ndarray, distance_matrix: np.ndarray, capacity: float) -> np.ndarray:
    archive_sorted = sorted(archive, key=lambda x: (-x[1][1], x[1][0]))
    base_solution = archive_sorted[0][0].copy()
    new_solution = [route.copy() for route in base_solution]

    depot_coords = coords[0]
    all_customers = np.array([c for route in new_solution for c in route[1:-1]])

    if len(all_customers) == 0:
        return new_solution

    customer_coords = coords[all_customers]
    vectors = customer_coords - depot_coords
    angles = np.arctan2(vectors[:, 1], vectors[:, 0])

    demand_threshold = capacity * 0.6
    high_demand_customers = all_customers[demand[all_customers] > demand_threshold]

    if len(high_demand_customers) < 2:
        return new_solution

    high_demand_coords = coords[high_demand_customers]
    high_demand_angles = np.arctan2(high_demand_coords[:, 1] - depot_coords[1], high_demand_coords[:, 0] - depot_coords[0])

    clusters = []
    used = set()

    for i, angle in enumerate(high_demand_angles):
        if i in used:
            continue

        cluster = [high_demand_customers[i]]
        used.add(i)

        for j, other_angle in enumerate(high_demand_angles):
            if j in used:
                continue

            angle_diff = min(abs(angle - other_angle), 2*np.pi - abs(angle - other_angle))
            if angle_diff < np.pi/4:
                dist = np.linalg.norm(high_demand_coords[i] - high_demand_coords[j])
                if dist < 2 * np.mean(distance_matrix[0, high_demand_customers]):
                    cluster.append(high_demand_customers[j])
                    used.add(j)

        if len(cluster) > 1:
            clusters.append(cluster)

    if not clusters:
        return new_solution

    best_cluster = max(clusters, key=lambda x: len(x))
    best_cluster_demand = sum(demand[best_cluster])

    if best_cluster_demand > capacity:
        return new_solution

    new_route = np.concatenate([[0], best_cluster, [0]])
    new_solution.append(new_route)

    for c in best_cluster:
        for i, route in enumerate(new_solution[:-1]):
            if c in route:
                new_solution[i] = np.delete(route, np.where(route == c)[0][0])
                break

    for i in reversed(range(len(new_solution))):
        if len(new_solution[i]) <= 2:
            new_solution.pop(i)

    return new_solution

def select_neighbor(archive: List[Tuple[np.ndarray, Tuple[float, float]]], coords: np.ndarray, demand: np.ndarray, distance_matrix: np.ndarray, capacity: float) -> np.ndarray:
    archive_sorted = sorted(archive, key=lambda x: (-x[1][1], -x[1][0]))
    base_solution = archive_sorted[0][0].copy()
    new_solution = [route.copy() for route in base_solution]

    if len(new_solution) < 2:
        return new_solution

    longest_route_idx = max(range(len(new_solution)), key=lambda i: len(new_solution[i]))
    longest_route = new_solution[longest_route_idx]

    if len(longest_route) <= 3:
        return new_solution

    min_dist = float('inf')
    best_merge_idx = -1
    for i in range(len(new_solution)):
        if i == longest_route_idx:
            continue
        other_route = new_solution[i]
        if len(other_route) <= 2:
            continue
        dist = distance_matrix[longest_route[-2]][other_route[1]]
        if dist < min_dist:
            min_dist = dist
            best_merge_idx = i

    if best_merge_idx == -1:
        return new_solution

    best_route = new_solution[best_merge_idx]
    merged_route = np.concatenate([longest_route[:-1], best_route[1:]])

    if np.sum(demand[merged_route[1:-1]]) > capacity:
        return new_solution

    new_solution[longest_route_idx] = merged_route
    del new_solution[best_merge_idx]

    customers_to_reinsert = merged_route[1:-1]
    np.random.shuffle(customers_to_reinsert)

    for cust in customers_to_reinsert:
        best_route_idx = -1
        best_pos = -1
        best_score = float('inf')

        for i in range(len(new_solution)):
            route = new_solution[i]
            for pos in range(1, len(route)):
                temp_route = np.concatenate([route[:pos], [cust], route[pos:]])
                if np.sum(demand[temp_route[1:-1]]) > capacity:
                    continue

                current_dist = 0
                for j in range(len(temp_route) - 1):
                    current_dist += distance_matrix[temp_route[j]][temp_route[j+1]]

                if current_dist < best_score:
                    best_score = current_dist
                    best_route_idx = i
                    best_pos = pos

        if best_route_idx != -1:
            new_solution[best_route_idx] = np.concatenate([
                new_solution[best_route_idx][:best_pos],
                [cust],
                new_solution[best_route_idx][best_pos:]
            ])

    return new_solution
