def select_neighbor(archive: List[Tuple[np.ndarray, Tuple[float, float]]], weight_lst: np.ndarray, value1_lst: np.ndarray, value2_lst: np.ndarray, capacity: float) -> np.ndarray:
    if not archive:
        raise ValueError("Archive is empty")

    # Select the solution with the most unbalanced objectives
    selected_idx = 0
    max_balance_diff = -float('inf')
    for i, (sol, obj) in enumerate(archive):
        obj1, obj2 = obj
        balance_diff = abs(obj1 - obj2) / max(1, obj1 + obj2)
        if balance_diff > max_balance_diff:
            max_balance_diff = balance_diff
            selected_idx = i

    base_solution = archive[selected_idx][0].copy()
    new_solution = base_solution.copy()
    current_weight = np.sum(weight_lst[new_solution == 1])

    # Calculate trade-off sensitivity for each item
    obj1_total, obj2_total = archive[selected_idx][1]
    trade_off = (value1_lst / (value2_lst + 1e-6)) * (obj2_total / (obj1_total + 1e-6))

    # Value-weighted perturbation
    items = np.arange(len(base_solution))
    np.random.shuffle(items)

    for item in items:
        if new_solution[item] == 1:
            # Try removing item
            new_weight = current_weight - weight_lst[item]
            if new_weight <= capacity:
                # Remove if it improves trade-off
                if trade_off[item] > 1.5 or (np.random.random() < 0.3 and trade_off[item] > 0.8):
                    new_solution[item] = 0
                    current_weight = new_weight
                    break
        else:
            # Try adding item
            new_weight = current_weight + weight_lst[item]
            if new_weight <= capacity:
                # Add if it improves trade-off
                if trade_off[item] < 0.5 or (np.random.random() < 0.3 and trade_off[item] < 1.2):
                    new_solution[item] = 1
                    current_weight = new_weight
                    break

    # Additional diversification step
    if np.random.random() < 0.2:
        # Randomly flip a small number of items to maintain diversity
        flip_indices = np.random.choice(len(base_solution), size=min(2, len(base_solution)), replace=False)
        for idx in flip_indices:
            if new_solution[idx] == 1:
                if current_weight - weight_lst[idx] <= capacity:
                    new_solution[idx] = 0
                    current_weight -= weight_lst[idx]
            else:
                if current_weight + weight_lst[idx] <= capacity:
                    new_solution[idx] = 1
                    current_weight += weight_lst[idx]

    return new_solution
