def select_neighbor(archive: List[Tuple[np.ndarray, Tuple[float, float]]], weight_lst: np.ndarray, value1_lst: np.ndarray, value2_lst: np.ndarray, capacity: float) -> np.ndarray:
    if not archive:
        raise ValueError("Archive is empty")

    # Step 1: Select the most promising solution (highest combined objective value)
    objectives = np.array([obj for (sol, obj) in archive])
    combined_values = objectives[:, 0] + objectives[:, 1]
    best_idx = np.argmax(combined_values)
    base_solution = archive[best_idx][0].copy()

    # Step 2: Generate a neighbor using a hybrid local search
    new_solution = base_solution.copy()
    n_items = len(weight_lst)

    # Strategy 1: Probabilistic swap based on marginal contribution
    for _ in range(min(3, n_items // 2)):
        candidates = np.where(new_solution == 1)[0]
        if len(candidates) == 0:
            break
        i = np.random.choice(candidates)
        if new_solution[i] == 1 and np.random.rand() < 0.7:  # Higher probability to remove
            new_solution[i] = 0
            # Try to add a new item that improves both objectives
            remaining_items = np.where(new_solution == 0)[0]
            if len(remaining_items) > 0:
                potential_adds = []
                for j in remaining_items:
                    if (weight_lst[j] <= capacity - np.sum(weight_lst[new_solution == 1])):
                        potential_adds.append(j)
                if potential_adds:
                    j = np.random.choice(potential_adds)
                    new_solution[j] = 1

    # Strategy 2: Guided perturbation near knee point (approximate)
    if len(archive) > 1:
        # Approximate knee point as solution with max (v1 - v2)
        knee_idx = np.argmax(objectives[:, 0] - objectives[:, 1])
        knee_solution = archive[knee_idx][0]
        # Perturb around knee items
        for i in range(n_items):
            if knee_solution[i] != new_solution[i] and np.random.rand() < 0.3:
                if knee_solution[i] == 1 and (weight_lst[i] <= capacity - np.sum(weight_lst[new_solution == 1])):
                    new_solution[i] = 1
                elif new_solution[i] == 1:
                    new_solution[i] = 0

    # Ensure feasibility
    current_weight = np.sum(weight_lst[new_solution == 1])
    if current_weight > capacity:
        # Strategy 3: Capacity-aware random walk
        excess = current_weight - capacity
        excess_items = np.where(new_solution == 1)[0]
        np.random.shuffle(excess_items)
        for i in excess_items:
            if weight_lst[i] <= excess:
                new_solution[i] = 0
                excess -= weight_lst[i]
                if excess <= 0:
                    break

    return new_solution
