def select_neighbor(archive: List[Tuple[np.ndarray, Tuple[float, float]]], weight_lst: np.ndarray, value1_lst: np.ndarray, value2_lst: np.ndarray, capacity: float) -> np.ndarray:
    if not archive:
        return np.zeros_like(weight_lst, dtype=int)

    # Select solution based on hybrid of dominance and density
    dominance_scores = []
    for sol, obj in archive:
        dominated_count = 0
        for other_sol, other_obj in archive:
            if (other_obj[0] >= obj[0] and other_obj[1] >= obj[1] and (other_obj[0] > obj[0] or other_obj[1] > obj[1])):
                dominated_count += 1
        density = np.sum(np.abs(sol - archive[0][0]))
        dominance_scores.append((len(archive) - dominated_count) * (1 + density))

    selected_idx = np.argmax(dominance_scores)
    base_solution = archive[selected_idx][0].copy()
    new_solution = base_solution.copy()
    current_weight = np.sum(weight_lst * new_solution)

    # Multi-phase local search
    # Phase 1: Probabilistic item replacement
    for i in range(len(weight_lst)):
        if new_solution[i] == 1 and random.random() < 0.3:
            new_weight = current_weight - weight_lst[i]
            if new_weight <= capacity:
                new_solution[i] = 0
                current_weight = new_weight

    # Phase 2: Value-weighted neighborhood exploration
    value_weights = value1_lst + value2_lst
    sorted_indices = np.argsort(-value_weights)

    for i in sorted_indices[:min(10, len(weight_lst))]:
        if new_solution[i] == 0:
            new_weight = current_weight + weight_lst[i]
            if new_weight <= capacity:
                # Probability based on value and remaining capacity
                prob = (value_weights[i] / (weight_lst[i] + 1e-6)) * ((capacity - current_weight) / capacity)
                if random.random() < prob:
                    new_solution[i] = 1
                    current_weight = new_weight

    # Phase 3: Dynamic capacity adjustment
    if current_weight > capacity:
        excess = current_weight - capacity
        included_items = np.where(new_solution == 1)[0]
        if len(included_items) > 0:
            # Remove items with lowest value-to-weight ratio until feasible
            item_ratios = (value1_lst + value2_lst) / (weight_lst + 1e-6)
            sorted_removals = np.argsort(item_ratios[included_items])
            for i in sorted_removals:
                if excess <= 0:
                    break
                if weight_lst[included_items[i]] <= excess:
                    new_solution[included_items[i]] = 0
                    excess -= weight_lst[included_items[i]]

    return new_solution

def select_neighbor(archive: List[Tuple[np.ndarray, Tuple[float, float]]], weight_lst: np.ndarray, value1_lst: np.ndarray, value2_lst: np.ndarray, capacity: float) -> np.ndarray:
    """
    Select a promising solution from the archive and generate a neighbor solution from it.

    Args:
    archive: List of (solution, objective) pairs. Each solution is a binary numpy array (0/1) of item selections.
             Each objective is a tuple of two float values (total value1, total value2).
    weight_lst: Numpy array of shape (N, ), item weights.
    value1_lst: Numpy array of shape (N, ), item values for objective 1.
    value2_lst: Numpy array of shape (N, ), item values for objective 2.
    capacity: Maximum allowed total weight.

    Returns:
    A new neighbor solution (numpy array).
    """
    if not archive:
        return np.zeros_like(weight_lst, dtype=int)

    # Selection based on objective balance and diversity
    balance_scores = []
    for sol, obj in archive:
        balance = min(obj[0], obj[1]) / (max(obj[0], obj[1]) + 1e-6)
        diversity = np.sum(sol) / len(sol)
        balance_scores.append(balance * (1 - diversity))

    selected_idx = np.argmax(balance_scores)
    base_solution = archive[selected_idx][0].copy()
    new_solution = base_solution.copy()
    current_weight = np.sum(weight_lst * new_solution)
    total_value1 = np.sum(value1_lst * new_solution)
    total_value2 = np.sum(value2_lst * new_solution)

    # Phase 1: Value-aware additions
    excluded_items = np.where(new_solution == 0)[0]
    if len(excluded_items) > 0:
        value_ratios = (value1_lst + value2_lst) / (weight_lst + 1e-6)
        candidate_items = excluded_items[np.argsort(-value_ratios[excluded_items])[:min(5, len(excluded_items))]]

        for i in candidate_items:
            if (current_weight + weight_lst[i]) <= capacity:
                new_solution[i] = 1
                current_weight += weight_lst[i]
                total_value1 += value1_lst[i]
                total_value2 += value2_lst[i]

    # Phase 2: Capacity-constrained removals
    if current_weight > capacity * 0.9:
        included_items = np.where(new_solution == 1)[0]
        if len(included_items) > 0:
            removal_ratios = (value1_lst + value2_lst) / (weight_lst + 1e-6)
            candidate_removals = included_items[np.argsort(removal_ratios[included_items])[:min(3, len(included_items))]]

            for i in candidate_removals:
                if (current_weight - weight_lst[i]) >= 0:
                    new_solution[i] = 0
                    current_weight -= weight_lst[i]
                    total_value1 -= value1_lst[i]
                    total_value2 -= value2_lst[i]

    # Phase 3: Objective-balancing swaps
    if abs(total_value1 - total_value2) > 0.2 * (total_value1 + total_value2):
        if total_value1 > total_value2:
            high_value1_items = np.where((new_solution == 1) & (value1_lst > value2_lst))[0]
            if len(high_value1_items) > 0:
                for i in high_value1_items:
                    for j in np.where(new_solution == 0)[0]:
                        if (current_weight - weight_lst[i] + weight_lst[j]) <= capacity:
                            if (value2_lst[j] - value1_lst[i]) > 0:
                                new_solution[i], new_solution[j] = new_solution[j], new_solution[i]
                                break
        else:
            high_value2_items = np.where((new_solution == 1) & (value2_lst > value1_lst))[0]
            if len(high_value2_items) > 0:
                for i in high_value2_items:
                    for j in np.where(new_solution == 0)[0]:
                        if (current_weight - weight_lst[i] + weight_lst[j]) <= capacity:
                            if (value1_lst[j] - value2_lst[i]) > 0:
                                new_solution[i], new_solution[j] = new_solution[j], new_solution[i]
                                break

    return new_solution
