    if not candidates:
        base_solution = archive[0][0].copy()
    else:
        base_solution = random.choice(candidates).copy()

    new_solution = base_solution.copy()

    # Apply a hybrid local search strategy:
    # 1. Randomly select a subset of items (up to 4) to flip, prioritizing items with high marginal contribution
    n_items = len(weight_lst)
    current_weight = np.sum(weight_lst * base_solution)
    current_value1 = np.sum(value1_lst * base_solution)
    current_value2 = np.sum(value2_lst * base_solution)

    if n_items > 0:
        # Calculate marginal contributions for each item
        marginal_contribs = []
        for i in range(n_items):
            if base_solution[i] == 1:
                marginal_contribs.append((0.6 * value1_lst[i] + 0.4 * value2_lst[i], i))
            else:
                if current_weight + weight_lst[i] <= capacity:
                    marginal_contribs.append((0.6 * value1_lst[i] + 0.4 * value2_lst[i], i))
                else:
                    marginal_contribs.append((-float('inf'), i))

        # Sort by marginal contribution (descending)
        marginal_contribs.sort(reverse=True, key=lambda x: x[0])
        top_indices = [i for _, i in marginal_contribs[:min(4, n_items)]]

        # Randomly select a subset of these top items to flip
        num_flips = random.randint(1, min(4, len(top_indices)))
        flip_indices = random.sample(top_indices, num_flips)

        total_change = 0
        for i in flip_indices:
            if new_solution[i] == 1:
                total_change -= weight_lst[i]
            else:
