        for i in sorted_indices:
            if new_solution[i] == 0 and current_weight + weight_lst[i] <= capacity:
                new_solution[i] = 1
                break

    # Additionally, try swapping two items if possible
    if np.array_equal(new_solution, base_solution) and n_items >= 2:
        # Find the best swap that improves both objectives
        best_improvement = -float('inf')
        best_i, best_j = -1, -1

        for i in range(n_items):
            for j in range(i + 1, n_items):
                if new_solution[i] != new_solution[j]:
                    if new_solution[i] == 0 and new_solution[j] == 1:
                        if current_weight + weight_lst[i] - weight_lst[j] <= capacity:
                            delta_value1 = value1_lst[i] - value1_lst[j]
                            delta_value2 = value2_lst[i] - value2_lst[j]
                            improvement = 0.6 * delta_value1 + 0.4 * delta_value2
                            if improvement > best_improvement:
                                best_improvement = improvement
                                best_i, best_j = i, j
                    else:
